// sketch
function face(x) {

  fill(eleventh_parameter)
  beginShape()
  bezier(
    0, height - third_parameter/2 ,
   x - 2*first_parameter, height-third_parameter,
    x + 2*first_parameter, height-third_parameter,
    width, height- third_parameter/2)
  vertex(0,height)
  vertex(width,height)
  vertex(width, height - third_parameter/2 - 5)
  endShape()

  //chin
  fill(sixth_parameter)
  beginShape();
  vertex(x - first_parameter, 100);
  bezierVertex(
    x - first_parameter,
    third_parameter,
    x + first_parameter,
    third_parameter,
    x + first_parameter,
    100
  );
  endShape();

  //eyes
  strokeWeight(2)
  fill("#fff")
  beginShape();
  vertex(x - first_parameter/4, 120)
  vertex(x - first_parameter/2, 120)
  bezierVertex(
    x - 2*first_parameter/3, 120,
    x - first_parameter/5, 150,
    x - first_parameter/4, 120)

  endShape();
  beginShape()  
  vertex(x + first_parameter/2, 120)
  vertex(x + first_parameter/4, 120)
  bezierVertex(
    x + first_parameter/5, 150,
    x + 2*first_parameter/3, 120,
    x + first_parameter/2, 120)
  endShape();

  //pupil and "nose"
  strokeWeight(5)
  point(x + 2*first_parameter/5 - 5, 123)
  point(x - 2*first_parameter/5 + 5, 123)
  line(x, 130, x + first_parameter/8, 160 )

  //eyebrows
  stroke("#000")
  //right
  beginShape()
  vertex(x + first_parameter/2, 115)
  vertex(x + first_parameter/4, 118)
  endShape()

  //left
  beginShape()
  vertex(x - first_parameter/2, 115)
  vertex(x - first_parameter/4, 118)
  endShape()
}


function bandana(x){

  stroke("#000")
  fill(twelfth_parameter)
  beginShape()
  vertex(x + first_parameter/8, 150)
  vertex(x + first_parameter - 5, 160)
  bezierVertex(
    x + 3*first_parameter/4, third_parameter,
    x + first_parameter/6, third_parameter - 10,
    x + first_parameter/8, third_parameter)
    bezierVertex(
    x + first_parameter/3, 8*third_parameter/10,
    x - first_parameter, 8*third_parameter/10,
    x - first_parameter - 5, 160)

  vertex(x - first_parameter - 5, 160)
  vertex(x + first_parameter/8, 150)
  endShape()

}

function hat(x) {

  let hatOutline = color(tenth_parameter)

  fill(tenth_parameter)
  //top
  strokeWeight(2);
  beginShape();
  vertex(x - first_parameter, 50 - second_parameter); //top left
  vertex(x + first_parameter, 50 - second_parameter); //top right
  vertex(x + first_parameter, 80); //bottom right
  vertex(x - first_parameter, 80); //bottom left
  vertex(x - first_parameter, 50 - second_parameter); //top left
  endShape();

  stroke("#000");
  strokeWeight(5);

  beginShape();
  vertex(x + first_parameter, 52 - second_parameter); //top right
  vertex(x + first_parameter, 80); //bottom right
  vertex(x - first_parameter, 80); //bottom left
  endShape();

  //brim
  beginShape();
  vertex(x + first_parameter, 60);
  bezierVertex(
    x + 40 + first_parameter,
    60,
    x + 70 + first_parameter,
    70,
    x + 50 + first_parameter,
    85
  );
  bezierVertex(x + 105, 110, x - 15, 110, x - 50 - first_parameter, 95);
  bezierVertex(
    x - 100 - first_parameter,
    85,
    x - 100 - first_parameter,
    60,
    x - first_parameter,
    60
  );
  endShape();

  stroke(tenth_parameter);
  beginShape();
  vertex(x - 30 - first_parameter, 85);
  bezierVertex(
    x - 100 - first_parameter,
    85,
    x - 100 - first_parameter,
    60,
    x + 10 - first_parameter,
    62
  );
  endShape();
}

