let noiseVal = 0;

function setup() {
  createCanvas(400, 400);
  noiseDetail(8); // Set noise detail for smoother results
}

function draw() {
  background(255);

  // Adjust noise values over time for animation
  noiseVal += 0.02;

  // Iterate over each pixel of the canvas
  for (let y = 0; y < height; y += 10) {
    for (let x = 0; x < width; x += 10) {
      // Calculate Perlin noise value for this pixel
      let val = noise(x * 0.01, y * 0.01, noiseVal) * TWO_PI;

      // Map the noise value to a color
      let col = map(val, 0, TWO_PI, 0, 255);

      // Draw colorful circles with varying sizes
      fill(col, 150, 200);
      noStroke();
      let diameter = map(sin(noiseVal), -1, 1, 5, 30); // Vary size with sine function
      ellipse(x, y, diameter, diameter);
    }
  }
}

