  for (let i = 0; i < 100; i++) { // Adjust number of raindrops as needed
    raindrops.push(new Raindrop());
  }
}

function draw() {
  background(0);
  drawImagePixels();
  drawRain();
}

function drawImagePixels() {
  const spacing = 500 / img.width;
  img.loadPixels();

  for (let y = 0; y < img.height; y++) {
    for (let x = 0; x < img.width; x++) {
      const in_color = img.get(x, y);
      const dot_size = (lightness(in_color) / 100) * 3;
      fill(in_color);
      ellipse(
        x * spacing + spacing * 0.5,
        y * spacing + spacing * 0.5,
        dot_size,
        dot_size
      );
    }
  }
}

function drawRain() {
  for (let i = 0; i < raindrops.length; i++) {
    raindrops[i].update();
    raindrops[i].draw();
  }
}

// Raindrop class
class Raindrop {
  constructor() {
    this.x = random(width);
    this.y = random(-height, 0);
    this.size = random(2, 5);
    this.speed = random(5, 10);
    this.alpha = random(50, 150);
  }

  update() {
    this.y += this.speed;
    // Reset the raindrop if it goes below the screen
    if (this.y > height) {
      this.y = random(-100, 0); // Start above the screen
      this.x = random(width);
      this.speed = random(5, 10);
      this.alpha = random(50, 150);
    }
  }

  draw() {
    // Drawing multiple ellipses with decreasing opacity to simulate a blur effect
    noStroke();
    for (let i = 0; i < 5; i++) {
      fill(255, 255, 255, this.alpha / (i + 1));
      ellipse(this.x, this.y - (this.speed * i) / 5, this.size - i, this.size - i);
    }
  }
}

