let colNum = 30;
let rowNum = 30;
let x, y;

function setup() {
  createCanvas(500, 500);
}

function draw() {
  background(0);
  drawSquares(20,20);

}

function drawSquares(x,y){

  const rotations = map(millis(), 0, 2000, 0, 1);
  const angle = map(rotations, 0, 1, 0, 2 * PI);
  const h = sin(angle) * 20;
  rectMode(CENTER);

    for(i=5; i<height; i+=rowNum){
      for(j=5; j<width; j+=colNum){
        let points = [];
        points.push(createVector(i, j));
        noiseDistort(points, 0.01, 40);
          if (h > 0) {
            fill("#bd98e0");
            rect(points[0].x, points[0].y, h, h, 2);
          } else {
            fill("#efa6b6");
            ellipse(points[0].x, points[0].y, h, h);
          }
        // rect(i, j, h, h)
    }
  }
}

function noiseDistort(inPoints, frequency, amplitude) {
  let outPoints = [];
  for (let i = 0; i < inPoints.length; i++) {
    let p = inPoints[i];

    p.x += (noise(p.x * frequency, p.y * frequency) - 0.5) * amplitude;
    p.y += (noise(p.x * frequency, p.y * frequency) - 0.5) * amplitude;

    outPoints.push(p);
  }
  return outPoints;
}

