function draw() {
  background(0);

  let noiseLevel = 100;
  let noiseScale = 0.1;

  img = createImage(30, 30);
  img.loadPixels();
  colorMode(HSB, 100);


  for (let y = 0; y < img.height; y++) {
    for (let x = 0; x < img.width; x++) {

      // Scale input coordinates.
      let nx = noiseScale * x;
      let ny = noiseScale * y;
      let nt = noiseScale * frameCount;
      // Compute noise value.
      let s = noiseLevel * noise(nx, ny, nt);

      console.log(s);
      let c = color(s*1.5, x, s*1.5);
      img.set(x, y, c);
    }
  }

  img.updatePixels();

  noSmooth();
  image(img, 0, 0, width, height);
  noLoop();
}

