// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
let pointCount = 1024;
let minDist = 10;
let maxDist = 5000;
let points = [];
let pointSize = 5;
let sphereRad = 150;
let sat = 100; //change to 50 to make it look pastely
let discoBall = true;  //set true to animate color using noise, set false for location mapped colors
let x=0;

function preload() {
  fireflies = loadSound("Owl City - Fireflies (Lyrics).mp3");
}

function setup() {
  let cnv = createCanvas(400, 400, WEBGL);
  cnv.mouseClicked(togglePlay);
  fft = new p5.FFT(0.5);
}

function draw() {
  let spectrum = fft.analyze();

  rotateY(x)
  background(0);
  push();
  stroke('black');
  noFill();
  sphere(sphereRad);
  pop();

  placePointsCullRange(pointCount, minDist, maxDist);
  for (let i = 0; i < points.length; i++) {
    let x = points[i].x;
    let y = points[i].y;
    let z = points[i].z;

    let h = map(x, -sphereRad, sphereRad, 0, 360);
    let s = map(y, -sphereRad, sphereRad, 0, 360);
    let b = map(z, -sphereRad, sphereRad, 0, 360);
    let n = noise(i, millis() * .001);
    let col = map(n, 0.1, 0.8, 0, 360);
    let br=spectrum[i];

    push();
    colorMode(HSB);
    noStroke();
    if (discoBall) {
      fill(col, sat, br);
    } else {
      fill(h, s, b);
    }
    translate(x, y, z);
    sphere(pointSize);
    pop();
  }
  x+=0.01
}

function placePointsCullRange(count, min_distance, max_distance) {
  let current_try = 0;
  let max_tries = count * 10;

  points.push({
    x: sin(0) * cos(0) * sphereRad,
    y: sin(0) * sin(0) * sphereRad,
    z: cos(0) * sphereRad
  });

  while (points.length < count && current_try < max_tries) {
    current_try++;

    let phi = random(0, TWO_PI);
    let theta = random(0, PI);
    let x = sin(theta) * cos(phi) * sphereRad;
    let y = sin(theta) * sin(phi) * sphereRad;
    let z = cos(theta) * sphereRad;

    let isTooClose = checkClose(x, y, z, points, min_distance);
    let isCloseEnough = checkClose(x, y, z, points, max_distance);

    if (!isTooClose && isCloseEnough) {
      points.push({
        x: x,
        y: y,
        z: z,
      });
    }
  }

  return points;
}

function checkClose(x, y, z, points, min_distance) {
  for (let i = 0; i < points.length; i++) {
    if (dist(points[i].x, points[i].y, points[i].z, x, y, z) < min_distance) {
      return true;
    }
  }
  return false;
}

