function pixelateImage(inputImage, outputImage, pixelBlockSize) {

  //make sure to load and update the image pixels
  for (let y = 0; y < inputImage.height; y += pixelBlockSize) {
    for (let x = 0; x < inputImage.width; x += pixelBlockSize) {

      const c = getAverageColor(inputImage, x, y, pixelBlockSize);

      setBlockColor(outputImage, x, y, pixelBlockSize, c);
    }
  }
}
function getAverageColor(img, startX, startY, blockSize) {

  let totalR = 0,
    totalG = 0,
    totalB = 0,
    totalA = 0;

  for (let y = startY; y < startY + blockSize; y++) {
    for (let x = startX; x < startX + blockSize; x++) {
      const c = getQuick(img, x, y);
      totalR += c[0];
      totalG += c[1];
      totalB += c[2];
      totalA += c[3];
    }
  }
  const numPixels = blockSize * blockSize;
  return [
    totalR / numPixels,
    totalG / numPixels,
    totalB / numPixels,
    totalA / numPixels,
  ];
}

function setBlockColor(img, startX, startY, blockSize, c) {
  for (let y = startY; y < startY + blockSize; y++) {
    for (let x = startX; x < startX + blockSize; x++) {
      setQuick(img, x, y, c);
    }
  }
}



