var x, y;
var prev;
var c;
function setup() {
  createCanvas(400, 400);
  x = -10;
  y = height - 105;
  strokeWeight(2);
  c='white';
}

function draw() {
  background('#E8E238');
  const theta = map(frameCount, 0, 60, 0, 2 * PI);

  stickFigure(x, y, theta);

  x += 1;
  if (x > width + 10) {

    x = -10;
  }
}

function stickFigure(x, y, theta) {
  const r = 10;
  const delta_x = sin(theta) * 12;
  px = x - abs(delta_x);
  py = y + r + 50;

  push();
  noStroke();
if((delta_x>0 && prev<0)||(delta_x<0 && prev>0)){
  c=color(random(0,255),random(0,255),random(0,255));
 }
 fill(c);
//else if(delta_x<0 && prev>0){
//   fill('blue');
// }
  beginShape();
  vertex(0, y-r);
  vertex(x, y-r);
  vertex(x, py);
  vertex(px, height);
  vertex(0, height);
  endShape();
  pop();

  //head and torso
  circle(x, y, 2 * r);
  line(x, y + r, x, py);

  //legs
  line(x, py, x + delta_x, py + 45);
  line(x, py, x - delta_x, py + 45);

  const delta_a = sin(theta / 2) * 12;
  //arms
  line(x, py - 40, x + delta_a, py - 5);
  line(x, py - 40, x - delta_a, py - 5);
  prev=delta_x;
}

