const params = {
  noiseLevel: 10,
  noiseScale: 0.005,
  material: 1,
  lightX: 0,
  lightY: 0,
  lightZ: 0,
  hue: 150
};
var spacing;
var total;
var n;
const pane = new Tweakpane.Pane();
pane.addInput(params, "noiseLevel", { min: minnoiseLevel, max: maxnoiseLevel });
pane.addInput(params, "noiseScale", { min: 0.001, max: 0.01 });
pane.addInput(params, "lightX", { min: -200, max: 200 });
pane.addInput(params, "lightY", { min: -200, max: 200 });
pane.addInput(params, "lightZ", { min: -100, max: 100 });
pane.addInput(params, "hue", { min: 0, max: 300 });
pane.addInput(params, 'material', {
  options: {
    normal: 1,
    ambient: 2,
    specular: 3
  },
});
function setup() {
  createCanvas(400, 400, WEBGL);
  spacing = params.noiseLevel * 2;
  total = width / (spacing);
}

function draw() {
  colorMode(HSB);
  if (params.material == 1) {
    normalMaterial();
  } else if (params.material == 2) {
    ambientMaterial(params.hue);
  }
  else if (params.material == 3) {
    specularMaterial(params.hue);
  }
  background(0);
  translate(-width / 2 - spacing / 2, -height / 2 - spacing / 2);
  let xpos = 0;
  let ypos = 0;
  for (let i = 0; i < total; i++) {
    translate(0, spacing);
    for (let j = 0; j < total; j++) {
      n = noise(xpos * params.noiseScale, ypos * params.noiseScale, millis() * .001);
      translate(spacing, 0);
      pointLight(params.hue, 100, 100, params.lightX, params.lightY, params.lightZ);
      sphere(params.noiseLevel * n);
      xpos += spacing;
    }
    translate(-total * spacing, 0);
    ypos += spacing;
  }
}

