let ns;
let cs;

function setup() {
  createCanvas(600, 600);
}

function draw() {
  background(30);

  ns = params.noiseScale * frameCount/10;
  cs = params.noiseLevel * noise(params.circleSpacing, ns, params.noiseOffset);

  console.log(cs);

  drawRose(width / 2, height / 2, params.numPetals, params.numCircles, cs, ns);
}

function drawRose(x, y, numPetals, numCircles, circleSpacing, noiseOffset) {
  let angleIncrement = TWO_PI / numPetals;
  let maxRadius = 100; 

  noFill();

  strokeWeight(2);

  for (let j = 0; j < numPetals; j++) {
    let angle = j * angleIncrement;
    for (let i = 0; i < numCircles; i++) {
      let newSize = i * circleSpacing;
      let noiseValue = noise(noiseOffset + i * 1);
      let newSizeWithNoise = newSize + map(noiseValue, 0, 1, -10, 10); 
      let radius = newSizeWithNoise + maxRadius * sin(numPetals * angle);
      let posX = x + radius * cos(angle);
      let posY = y + radius * sin(angle);

      stroke(noiseValue*i*50, radius, newSizeWithNoise*10 );

      ellipse(posX, posY, newSizeWithNoise, newSizeWithNoise);
    }
  }
}

let img1; // Variable to store the image
let pixelSizeSlider, spacingSlider, depthSlider; // Sliders for voxel size, spacing, and depth

function preload() {
  // Preload the image
  img1 = loadImage("https://i.imgur.com/6aqThh3.jpeg");
}

function setup() {
  createCanvas(windowWidth, windowHeight - 100, WEBGL);
  img1.resize(50, 0); // Resize the image to make the rendering manageable
  img1.loadPixels(); // Load the pixel data of the image

  // Voxel Size Slider and Label
  createDiv('Pixle Size').position(10, height + 5).style('color', 'black').style('font-family', 'Roboto, sans-serif');
  pixelSizeSlider = createSlider(1, 20, 5);
  pixelSizeSlider.position(10, height + 20);
  pixelSizeSlider.style('width', '100px');

  // Spacing Slider and Label
  createDiv('Spacing').position(10, height + 35).style('color', 'black').style('font-family', 'Roboto, sans-serif');
  spacingSlider = createSlider(5, 20, 10);
  spacingSlider.position(10, height + 50);
  spacingSlider.style('width', '100px');

  // Depth Slider and Label
  createDiv('Depth').position(10, height + 65).style('color', 'black').style('font-family', 'Roboto, sans-serif');
  depthSlider = createSlider(0, 100, 50);
  depthSlider.position(10, height + 80);
  depthSlider.style('width', '100px');

  // Attach input event listeners to sliders for real-time updates
  pixelSizeSlider.input(() => redraw());
  spacingSlider.input(() => redraw());
  depthSlider.input(() => redraw());


  noLoop(); // Only redraw when necessary
}

function draw() {
  background(0);
  // Check if either slider is at its minimum value
  if (pixelSizeSlider.value() === pixelSizeSlider.elt.min || spacingSlider.value() === spacingSlider.elt.min) {
    // If so, draw the original image
    // Adjust the image drawing to fit the WEBGL context
    push();
    translate(-width / 2, -height / 2, 0); // Move back to default 2D canvas coordinates
    image(img1, 0, 0);
    pop();
  } else {
    // Otherwise, render the voxel representation
    orbitControl();

    let pixelSize = pixelSizeSlider.value();
    let spacing = spacingSlider.value();

    // Center the image on the canvas
    translate(-img1.width * spacing / 2 + pixelSize / 2, -img1.height * spacing / 2 + pixelSize / 2, 0);

    for (let x = 0; x < img1.width; x++) {
      for (let y = 0; y < img1.height; y++) {
        let index = (x + y * img1.width) * 4;
        let r = img1.pixels[index];
        let g = img1.pixels[index + 1];
        let b = img1.pixels[index + 2];

        // Adjust depth based on pixel brightness
        let brightness = (r + g + b) / 3;
        let depth = map(brightness, 0, 255, -depthSlider.value(), depthSlider.value());

        push();
        // Adjust translation for voxel position using spacing for X and Y, and depth for Z
        translate(x * spacing, y * spacing, depth);
        fill(r, g, b);
        noStroke();
        box(pixelSize); // Draw each voxel with the size from the slider
        pop();
      }
    }
  }
}

