  noLoop();
}

var x1, y1, x2, y2, theta, theta2, old_l1, old_l2, old_speed1, old_speed2, old_strokeWeight;
const params = {
  l1: 100,
  l2: 50,
  speed1: 1,
  speed2: 5,
  stroke_weight: 10
}
const pane = new Tweakpane.Pane();
pane.addInput(params, "l1", { min: 50, max: 150 }); //controls length of line 1
pane.addInput(params, "l2", { min: 25, max: 100 }); //controls length of line 2
pane.addInput(params, "speed1", { min: 0.25, max: 1.5 }); //controls rotational speed of line 1
pane.addInput(params, "speed2", { min: 3, max: 6 });  //controls rotational speed of line 2
pane.addInput(params, "stroke_weight", { min: 1, max: 20 }); //controls length of line 1
function setup() {
  createCanvas(400, 400);
  theta = 0;
  theta2 = 0;
}

function draw() {
  //checks if tweakpane values have changed and redraws background to clear canvas
  if (
    old_l1 != params.l1 ||
    old_l2 != params.l2 ||
    old_speed1 != params.speed1 ||
    old_speed2 != params.speed2 ||
    old_strokeWeight != params.stroke_weight
  ) {
    background('white');
  }

  //endpoints of line1 and line 2 are functions of theta
  x1 = (params.l1) * sin(theta);
  y1 = (params.l1) * cos(theta);
  x2 = (params.l2) * sin(theta2);
  y2 = (params.l2) * cos(theta2);

  translate(width / 2, height / 2); //translate origin to center of canvas
  line(0, 0, x1, y1); //draws line from 0 to x1 and y1
  push();
  translate(x1, y1);  //translate origin to x1 y1
  line(0, 0, x2, y2); //draws line from new origin to x2 and y2
  strokeWeight(params.stroke_weight); //stroke weight of end point
  point(x2, y2);  //draws point at the end of line 2
  pop();
  theta += radians(params.speed1);  //theta is varied with speed
  theta2 += radians(params.speed2);

  //stores old slider values
  old_l1 = params.l1;
  old_l2 = params.l2;
  old_speed1 = params.speed1;
  old_speed2 = params.speed2;
  old_strokeWeight = params.stroke_weight;
}

