  strokeWeight(lineWeight);

  // move to starting position (without drawing)
  myTurtle.penUp();
  myTurtle.moveTo(width / 2, height / 2);

  // put the pen down to draw
  myTurtle.penDown();

  // chatGPT helped with this part but I made it a parameter and added millis to create the previous looping rotation effect
  //Set the turtle's bearing to a fixed angle
  myTurtle.turnTo(rotation * millis());

  // draw the star
  myTurtle.turnRight(72 + inc / 2);

  for (let i = 0; i < amount; i++) {
    let turtle = new Turtle();
    myTurtle.penUp();
    x =  width * noise(random(0, width))
    y = height * noise(random(0, height))

    myTurtle.moveTo(x, y);
    myTurtle.penDown();

    for (let lines = 0; lines < 5; lines++) {
      myTurtle.turnRight(-inc);
      for (let j = 0; j < inc; j++) {
        myTurtle.moveForward(flowerSize / inc);
        myTurtle.turnRight(incAngle);
      }
      myTurtle.turnRight(cornerAngle);
    }
  }
}

// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js

function setup() {
  createCanvas(800, 600);
  frameRate(30);
}

function draw() {
  let frame = frameCount % 90; // Loop the animation every 90 frames
  background(0);

  if (frame < 30) {
    // Beginning: Form the Sierpinski triangle
    drawSierpinski(width / 2, height / 2, 150, frame / 30);
  } else if (frame < 60) {
    // Middle: Show the text
    fill(255, map(frame, 30, 59, 0, 255));
    textSize(32);
    textAlign(CENTER, CENTER);
    text("Computational Form", width / 2, height / 2);
  } else {
    // End: Combine and fade out
    drawSierpinski(width / 2, height / 2, 150, 1);
    fill(255, map(frame, 60, 89, 255, 0));
    textSize(32);
    text("Computational Form", width / 2, height / 2);
  }
}

function drawSierpinski(x, y, size, progress) {
  let levels = floor(5 * progress); // Gradually increase detail level
  sierpinski(x, y, size, levels);
}

function sierpinski(x, y, size, depth) {
  if (depth === 0) {
    noFill();
    stroke(255);
    triangle(x, y - size / sqrt(3), x - size / 2, y + size / (2 * sqrt(3)), x + size / 2, y + size / (2 * sqrt(3)));
  } else {
    let newSize = size / 2;
    sierpinski(x, y - newSize / sqrt(3), newSize, depth - 1);
    sierpinski(x - newSize / 2, y + newSize / (2 * sqrt(3)), newSize, depth - 1);
    sierpinski(x + newSize / 2, y + newSize / (2 * sqrt(3)), newSize, depth - 1);
  }
}

