  stroke(c1);
  push();
  noStroke();
  fill('#523C12');
  rect(0, height - 50, width, 50);
  pop();
}

function draw() {
  drawBush(50, 470, 40);
  drawBush(250, 450, 30);
  drawBush(450, 470, 40);
  drawBush(160, 520, 20);
  drawBush(340, 520, 20);
}

function drawBush(x, y, length) {
  myTurtle.penUp();
  myTurtle.moveTo(x, y);
  myTurtle.turnTo(-90);
  myTurtle.penDown();
  drawBranch(length);
}
function drawBranch(length) {
  changeColor(length);
  if (length < 3) {
    return;
  }
  // draw this branch
  myTurtle.moveForward(length);

  // left child
  myTurtle.pushState();
  changeColor(length);
  myTurtle.turnLeft(50);
  drawBranch(length * 0.79);
  myTurtle.popState();

  // right child
  myTurtle.pushState();
  changeColor(length);
  myTurtle.turnRight(50);
  drawBranch(length * 0.79);
  myTurtle.popState();
}

function changeColor(length) {
  if (length < 20) {
    var amt = map(log(length), 1, 3, 0, 1);
    console.log(log(length), amt);
    var c4 = lerpColor(c2, c3, amt);
    stroke(c4);
  } else {
    stroke(c1);
  }
}

