function angleValueFromDeck() {
  // find the value at the current position in the deck
  var v = angleValues[anglePos];

  // change the position for next time
  anglePos++;

  // if that was the last value, shuffle and start over from the top
  if (anglePos == angleValues.length) {
    angleValues = shuffle(angleValues);
    anglePos = 0;
  }

  // return the value
  return v;
}

function lineLengthFromDeck() {
  // find the value at the current position in the deck
  var v = lineValues[linePos];

  // change the position for next time
  linePos++;

  // if that was the last value, shuffle and start over from the top
  if (linePos == lineValues.length) {
    lineValues = shuffle(lineValues);
    linePos = 0;
  }

  // return the value
  return v;
}

function colorFromDeck() {
  // find the value at the current position in the deck
  var v = colorValues[colorPos];

  // change the position for next time
  colorPos++;

  // if that was the last value, shuffle and start over from the top
  if (colorPos == colorValues.length) {
    colorValues = shuffle(colorValues);
    colorPos = 0;
  }

  // return the value
  return v;
}

