    flower.grow(); // Update the growth of each flower
    flower.display();
  }
}

class Flower {
  constructor(x, y, branches, hue, diameter) {
    this.x = x;
    this.y = y;
    this.branches = branches;
    this.angle = map(branches, 4, 25, 0.6, 0.1);
    this.hue = hue; // Hue value for the flower color
    this.startDiameter = diameter; // Initial diameter of the flower
    this.growthRate = 0.5; // Rate at which the flower grows (adjust as needed)
    this.currentDiameter = 0; // Current diameter of the flower
    this.growing = true; // Flag to indicate if the flower is still growing
  }

  display() {
    stroke(this.hue, 100, 100); // Set stroke color based on hue
    this.drawFlower(this.x, this.y, this.currentDiameter, this.branches);
  }

  drawFlower(x, y, diameter, branches) {
    let len = diameter / 2; // Length of the branch
    for (let i = 0; i < branches; i++) {
      push();
      translate(x, y); // Translate to the flower's position
      rotate(TWO_PI / branches * i);
      this.drawBranch(len);
      pop();
    }
  }

  drawBranch(len) {
    line(0, 0, 0, -len);
    translate(0, -len);
    if (len > 2) { // Adjusted minimum length
      push();
      rotate(this.angle);
      this.drawBranch(len * 0.67);
      pop();
      push();
      rotate(-this.angle);
      this.drawBranch(len * 0.67);
      pop();
    }
  }

  grow() {
    if (this.growing) {
      this.currentDiameter += this.growthRate; // Increase the diameter
      if (this.currentDiameter >= this.startDiameter) {
        this.growing = false; // Stop growing when reaching the initial diameter
      }
    }
  }
}

let spacingBetweenLoops, totalLoops;
let spiral1 = []; // Array to store vertices of the first spiral
let spiral2 = []; // Array to store vertices of the second spiral

function setup() {
  createCanvas(600, 600); // Set the size of the canvas
  background(30, 100, 0); // Set a greenish background
  noFill(); // Ensure the spiral has no fill
  stroke(180, 100, 0); // Set the spiral color to orange
  strokeWeight(2); // Set the width of the spiral line

  // Initialize variables
  spacingBetweenLoops = 10;
  totalLoops = 8;

  // Generate initial vertices for the spirals
  generateSpiral(width / 3, height / 2, spiral1, 1); // Clockwise rotation
  generateSpiral(width / 3 * 2, height / 2, spiral2, -1); // Counterclockwise rotation
}

function generateSpiral(startX, startY, spiral, direction) {
  let angle = 0;
  let radius = 10; // initialRadius
  for (let i = 0; i < totalLoops * 720; i++) {
    angle = radians(i * direction + frameCount); // Use frameCount for animation
    radius += spacingBetweenLoops / (100 * PI); // Increment radius
    let x = startX + radius * cos(angle);
    let y = startY + radius * -sin(angle);
    spiral.push(createVector(x, y)); // Store vertex in the array
  }
}

function updateSpiral(startX, startY, spiral, direction) {
  spiral.length = 0; // Clear previous vertices
  generateSpiral(startX, startY, spiral, direction); // Generate new vertices
}

function draw() {
  background(30, 100, 0); // Reset background

  // Update vertices of the spirals based on frameCount
  updateSpiral(width / 3, height / 2, spiral1, 1); // Clockwise rotation
  updateSpiral(width / 3 * 2, height / 2, spiral2, -1); // Counterclockwise rotation

  // Draw the first spiral
  beginShape();
  for (let i = 0; i < spiral1.length; i++) {
    let vertexPos = spiral1[i];
    vertex(vertexPos.x, vertexPos.y);
  }
  endShape();

  // Draw the second spiral
  beginShape();
  for (let i = 0; i < spiral2.length; i++) {
    let vertexPos = spiral2[i];
    vertex(vertexPos.x, vertexPos.y);
  }
  endShape();
}

