class Dot {
  constructor(frequency, speed, r, g, b, a) {
    this.frequency = frequency;
    this.time = 0;
    this.w = (random(width / 8, width) + random(width / 8, width)) / 1.5;
    this.h = (random(height / 8, height) + random(height / 8, height)) / 1.5;
    this.speed = speed;
    this.colorSpeed = speed;
    this.r = r;
    this.g = g;
    this.b = b;
    this.a = a;
  }

  make() {
    //where am i putting the dot
    this.xStart = noise(this.frequency) * this.w ;
    this.yStart = noise(this.frequency) * this.h ;
    this.diameter = noise(this.frequency) * 20;

    //move the dot
    this.x = this.xStart + noise(this.frequency) * this.time;
    this.y = this.yStart + noise(this.frequency) * this.time;

    //draw the dot
    this.selectColors();
    noStroke()
    fill(this.circleColor);
    ellipse(
      this.x,
      this.y,
      this.diameter,
      this.diameter
    );

    //change directions
    if (this.x < 15 ) {
      this.speed = abs(this.speed);
    } else if (this.x > width - 15) {
      this.speed = -abs(this.speed);
    }

    if (this.y < 15 ) {
      this.speed = abs(this.speed);
    } else if (this.y > height - 15) {
      this.speed = -abs(this.speed);
    }

    this.time += this.speed;
  }

  selectColors() {

    if (this.r < 15 ) {
      this.colorSpeed = abs(this.colorSpeed);
    } else if (this.r > 255) {
      this.colorSpeed = -abs(this.colorSpeed);
    }
    this.r += this.colorSpeed 
    this.circleColor = color(this.r, this.g, this.b, this.a);
  }
}

