  createSpan('Angle:').parent(controlsDiv);
  sliderAngle = createSlider(0, TWO_PI, PI / 4, 0.01).parent(controlsDiv);
  createSpan('Length Ratio:').parent(controlsDiv);
  sliderLengthRatio = createSlider(0.5, 0.8, 0.67, 0.01).parent(controlsDiv);
  createSpan('Stroke Weight:').parent(controlsDiv);
  sliderStrokeWeight = createSlider(1, 8, 2, 1).parent(controlsDiv);
}

function draw() {
  background(51);
  angle = sliderAngle.value();
  lengthRatio = sliderLengthRatio.value();
  strokeWeightValue = sliderStrokeWeight.value();
  strokeWeight(strokeWeightValue);
  translate(width * 0.5, height);
  branch(100);
  stroke(255);
}

function branch(len) {
  line(0, 0, 0, -len);
  translate(0, -len);
  if (len > 4) {
    push();
    rotate(angle);
    branch(len * lengthRatio);
    pop();
    push();
    rotate(-angle);
    branch(len * lengthRatio);
    pop();
  }
}

