const square = new Path.Rectangle({
    position: view.center,
    size: 300,
    fillColor: 'white'
});
//innercirc
const inner = new Path.Circle({
    center: view.center,
    radius: 100,
    fillColor: 'white'
});
//outercirc
const outer = new Path.Circle({
    center: view.center,
    radius: 140,
    fillColor: 'white'
});
//ring
const ring = outer.subtract(inner);
//operations array
const operations = ['unite', 'intersect', 'subtract', 'exclude', 'divide'];
//current position 
let curIndex = 0;
//current info for each activity
let operation, activeItem, result;
//timer to change operation
setInterval(setMode, 8000);
//update current activity
setMode();
function setMode() {
    operation = operations[curIndex % operations.length];
    curIndex++;
}
//mouse functions
function onMouseDown(event) {
    const hitResult = project.hitTest(event.point);
    activeItem = hitResult && hitResult.item;
}

function onMouseDrag(event) {
    if (activeItem) activeItem.position = event.point;
}

function onMouseUp() {
    activeItem = null;
    square.position = view.center;
}
//update canvas
function onFrame(event) {
    if (!activeItem || activeItem !== ring) {
        const offset = new Point(140, 80).multiply([Math.sin(event.count / 60), Math.sin(event.count / 40)]);
        ring.position = view.center.add(offset);
    }

    if (result) result.remove();

    if (curIndex < operations.length) {
        result = square[operation](ring);
    } else {
        result = ring[operation](square);
    }

    result.fillColor = Color.random();
}

function onKeyDown(event) {
    if (event.key === 'space') {
        curIndex++;
        operation = operations[curIndex % operations.length];
    }
}

