function rectLine(centerX, centerY, d) {
	stroke(255)
	strokeWeight(3)

	let startX = centerX + d
	let startY = centerY - d

	for (let i = 0; i < 400; i++) {
		stroke(i, random(i), random(i) * 255)

		let r = int(random(4))
		if (r == 0) { // to down
			strokeWeight(random(r))
			line(startX, startY, startX, startY + d)
			startY = startY + d
		} else if (r == 1) { // to left
			strokeWeight(random(r))
			line(startX, startY, startX - d, startY)
			startX = startX - d
		} else if (r == 2) { // to up
			strokeWeight(random(r))
			line(startX, startY, startX, startY - d)
			startY = startY - d
		} else if (r == 3) { // to right
			strokeWeight(random(r))
			line(startX, startY, startX + d, startY)
			startX = startX + d
		}

	}
}

