function drawPattern() {
  background(50);


  let numShapes = floor(sqrt(width * height) / 10);

  let size = min(width, height) / (numShapes * 1);
  let radius = size / 5;
  let angleIncrement = 360 / numShapes;
  myTurtle.penDown();

  function drawRecursive(size, remainingShapes) {
    if (remainingShapes === 0) return; // Base case: Stop recursion when no shapes remain

    let color = random(256); 
    stroke(color); 
    drawShape(radius); 

    myTurtle.moveForward(size);
    myTurtle.turnRight(angleIncrement);

    drawRecursive(size * 0.8, remainingShapes - 1);
  }

  drawRecursive(size, numShapes);
}

function drawShape(radius) {
  let numSides = 1000;
  let angle = 360 / numSides;

  for (let i = 0; i < numSides; i++) {
    myTurtle.moveForward(radius);
    myTurtle.turnRight(angle);
  }
}

let myEllipse;
let myTriangle;
let textX, textY;

function setup() {
	createCanvas(windowWidth, windowHeight);
	background(100);
	noFill();

	myEllipse = new Ellipse();
	myTriangle = new Triangle();

	textX = myTriangle.markerX1 + myTriangle.size1 / 2 - 50;
	textY = myTriangle.markerY1 - 20;
}

function draw() {
	colorMode(HSB);
	for (let o = 0; o < height; o += 5) {
		for (let i = 0; i < width; i += 5) {
			let c = color(int(noise(i / 150, o / 150) * 300 / 50) * 50, 130, 50);
			fill(c);
			rect(i, o, 20, 20);
			noStroke();
		}
	}

	myTriangle.display();
	myEllipse.display();

	strokeWeight(0.5);
	stroke(0, 10, 10);

	drawingContext.setLineDash([5, 5]); 

	noFill();
	beginShape();
	for (let t = 0; t < 1; t += 0.001) {
		let x = lerp(myTriangle.markerX1, myEllipse.markerX2, t);
		let y = lerp(myTriangle.markerY1, myEllipse.markerY2, t);
		x += noise(t * 20 + frameCount / 100) * 20 - 10;
		y += noise(t * 20 + frameCount / 100) * 20 - 10;
		vertex(x, y);
	}
	endShape();

	drawingContext.setLineDash([]);

	fill(255);
	text("TREASURE IS HERE!!!", textX, textY);
}

class Ellipse {
	constructor() {
		this.markerX2 = noise(0.01 - 100) * width;
		this.markerY2 = noise(0.01 - 100) * height;
		this.size2 = 15;
	}

	display() {
		fill(0, 10, 10);
		ellipse(this.markerX2, this.markerY2, this.size2);
	}
}

class Triangle {
	constructor() {
		this.markerX1 = noise(0.01) * width;
		this.markerY1 = noise(0.01 + 100) * height;
		this.size1 = 30;
	}

	display() {
		let colIndex1 = int(this.markerX1 / this.size1);
		let rowIndex1 = int(this.markerY1 / this.size1);

		fill(0, 100, 300);
		stroke(10);
		strokeWeight(2);
		triangle(colIndex1 * this.size1, rowIndex1 * this.size1,
			(colIndex1 + 0.5) * this.size1, (rowIndex1 + 1) * this.size1,
			(colIndex1 + 1) * this.size1, rowIndex1 * this.size1);
	}
}

