//mousePressed and Reshuffling help from ChatGPT
function mousePressed() {
  // Check if all shapes have been shown
  if (currentIndex < shapes.length) {
    // Draw the current shape
    shapes[currentIndex]();
    currentIndex++;
  } else {

    // Reshuffling
    background(230,230,230); // Clear the canvas
    textAlign(CENTER, CENTER);
    textSize(16);
    fill(0);
    textFont('Courier New');
    text("click for new flower...", width / 2, height / 2);
    setTimeout(function() {
      background(230,230,230); // Clear the "Reshuffling" message
      shuffleArray(shapes);
      currentIndex = 0;
    }, 600); // 
  }
}

