// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.min.js
// referencing Justin's example from https://compform.net/parameters/
// style inspired by Zach Lieberman's Expression Mirror

let eye_slider, mouth_slider, background_slider;

function preload() {
  eye_left = loadImage("assets/eye-left.png");
  eye_right = loadImage("assets/eye-right.png");
  mouth = loadImage("assets/mouth.png");
}

function setup() {
  createCanvas(600,600);

  fill(100);
  noStroke();
  rectMode(CENTER);
  ellipseMode(CENTER);
  imageMode(CENTER);


  // sliders
  createP("eyes");
  eye_slider = createSlider(20, 300, 134.29);

  createP("mouth");
  mouth_slider = createSlider(50, 600, 164);

  createP("background noise");
  background_slider = createSlider(0, 100, 50);

  // createP("background");
  // color_picker = createInput("#F9E007", "color");  

}

function draw() {
  let glitchAmount = background_slider.value() / 100.0;
  background(255);
  backgroundNoise(glitchAmount);
  face();
}


function face() {  
  let originalEyeWidth = 134.29; 
  let originalEyeHeight = 100;   

  let originalMouthSize = 164;   
  let originalMouthHeight = 88; 

  let eyes_width = eye_slider.value();
  let mouth_size = mouth_slider.value();

  // calculating proportional height for eyes
  let eyes_height = (originalEyeHeight / originalEyeWidth) * eyes_width;

  // calculating proportional height for mouth
  let mouth_height = (originalMouthHeight / originalMouthSize) * mouth_size;

  image(eye_left, width/3, height/3 + 50, eyes_width, eyes_height);
  image(eye_right, width - (width/3), height/3 + 50, eyes_width, eyes_height);
  image(mouth, width/2, height/2 + 80, mouth_size, mouth_height);
}


function backgroundNoise(amount) {
  noiseDetail(8, 0.5);
  for (let y = 0; y < height; y += 5) {
    for (let x = 0; x < width; x += 5) {
      let noiseVal = noise(x * 10, y * .1);
      if (noiseVal > amount) {
        fill(0);
        rect(x, y, 5, 5,10);
      }
    }
  }
}

paper.install(window);

window.onload = function () {

  // Get a reference to the canvas object
  var canvas = document.getElementById('myCanvas');
  canvas.style.width = '400px'
  canvas.style.height = '400px'

  // Create an empty project and a view for the canvas:
  paper.setup(canvas);


  var center = new Point(200, 200);

  for (let i = 0; i < 30; i++) {
    var w = 30 * (i + 1)
    var bound = new Rectangle(new Point(0, 0), new Point(w, w))
    var rectangle = new Path.Rectangle(bound);
    rectangle.strokeColor = "red";
    rectangle.strokeWidth = 2;
    rectangle.position = center;
    rectangle.rotate(i * 2)
  }


  // Draw the view now:
  paper.view.draw();
}

//inspired by https://openprocessing.org/sketch/1617170
//& circle grid in noise chapter from Justin Bakse https://compform.net/noise/

let factor = 0;
function setup() {
  createCanvas(600, 600);
  noLoop();
}

function draw() {
  background('#364652');
  noStroke(); 

  //  ellipses using noise //help from chatgpt 
  fill(236,228,183);
  let rez = random(0.005, 0.03); 
  factor += 1000;
  for (let i = 0; i < width; i += 10) {
    for (let j = 0; j < height; j += 20) {
      let n = noise(i * rez + factor, j * rez + factor);
      if (n > 0.5) {  
        let ellipseSize = n * 10; // size of the ellipse based on noise
        ellipse(i, j, ellipseSize, ellipseSize);
      }
    }
  }
for (let i = 0; i < width; i += 10) {
    for (let j = 0; j < height; j += 20) {
      let n = noise(i * rez + factor, j * rez + factor);
      if (n > 0.5) { // Threshold to create an ellipse
        let ellipseSize = n * 0.4; // Size of the ellipse based on noise
        ellipse(i, j, ellipseSize, ellipseSize);
