class Popup {
  constructor(title, content) {
    this.title = title;
    this.content = content;
    this.closeButton = this.createCloseButton();
    this.element = this.createPopupElement();
    this.resizeHandles = this.createResizeHandles();
    this.attachListeners();
  }

  createPopupElement() {
    const popup = document.createElement("div");
    popup.classList.add("popup");
    const titleBar = document.createElement("div");
    titleBar.classList.add("title-bar");
    titleBar.textContent = this.title;
    titleBar.appendChild(this.closeButton);
    popup.appendChild(titleBar);
    popup.appendChild(this.content);
    return popup;
  }

  createResizeHandles() {
    const handles = [];
    const positions = ["se"];
    positions.forEach((position) => {
      const handle = document.createElement("div");
      handle.classList.add("resize-handle", position);
      this.element.appendChild(handle);
      handles.push(handle);
    });
    return handles;
  }

  createCloseButton() {
    const closeButton = document.createElement("button");
    closeButton.innerText = "✕";
    closeButton.classList.add("close-button");
    return closeButton;
  }

  attachListeners() {
    this.element
      .querySelector(".title-bar")
      .addEventListener("mousedown", (event) => {
        this.startDrag(event);
      });

    document.addEventListener("mousemove", (event) => {
      this.drag(event);
    });

    document.addEventListener("mouseup", () => {
      this.endDrag();
    });

    this.closeButton.addEventListener("click", () => {
      this.close();
    });

    this.resizeHandles.forEach((handle) => {
      handle.addEventListener("mousedown", (event) => {
        this.startResize(event, handle);
      });
    });
  }

  startResize(event, handle) {
    event.preventDefault();
    this.isResizing = true;
    this.resizeHandle = handle;
    const rect = this.element.getBoundingClientRect();
    this.resizeStart = {
      x: event.clientX,
      y: event.clientY,
      width: rect.width,
      height: rect.height,
    };
    document.addEventListener("mousemove", this.resize);
    document.addEventListener("mouseup", this.endResize);
  }

  resize = (event) => {
    if (this.isResizing) {
      const deltaX = event.clientX - this.resizeStart.x;
      const deltaY = event.clientY - this.resizeStart.y;
      let newWidth = this.resizeStart.width + deltaX;
      let newHeight = this.resizeStart.height + deltaY;

      // Ensure minimum width and height
      newWidth = Math.max(newWidth, 100); // Minimum width
      newHeight = Math.max(newHeight, 100); // Minimum height

      this.element.style.width = newWidth + "px";
      this.element.style.height = newHeight + "px";
    }
  };

  endResize = () => {
    this.isResizing = false;
    document.removeEventListener("mousemove", this.resize);
    document.removeEventListener("mouseup", this.endResize);
  };

  startDrag(event) {
    this.isDragging = true;
    const rect = this.element.getBoundingClientRect();
    this.dragOffset = {
      x: event.clientX - rect.left,
      y: event.clientY - rect.top,
    };
  }

  drag(event) {
    if (this.isDragging) {
      const newX = event.clientX - this.dragOffset.x;
      const newY = event.clientY - this.dragOffset.y;
      this.element.style.left = newX + "px";
      this.element.style.top = newY + "px";
    }
  }

  endDrag() {
    this.isDragging = false;
  }

  show() {
    document.body.appendChild(this.element);
  }

  close() {
    document.body.removeChild(this.element);
  }
}
class File {
  constructor(name, id, left, top, instance, content) {
    this.name = name;
    this.id = id;
    this.element = document.createElement("div");
    this.file = document.createElement("i");
    this.setup(left, top);
    this.attachListeners();
    this.instance = instance; //the "files" class it is associated with
    this.content = content;
  }

  setup(left, top) {
    this.element.id = this.id;
    this.element.style.position = "absolute";
    this.element.style.left = `${left + 200}px`;
    this.element.style.top = `${top}px`;

    // Create file icon
    this.file.className = "fa-solid fa-file-lines fa-8x";
    // this.file.style.color = "#555";
    this.element.appendChild(this.file);

    // Create file label
    const nameElement = document.createElement("div");
    nameElement.innerText = this.name;
    nameElement.style.textAlign = "center";
    this.element.appendChild(nameElement);

    document.getElementById("iconContainer").appendChild(this.element);

    this.calculateBounds();
    console.log(this.xLeft, this.xRight, this.yTop, this.yBottom);
  }

  calculateBounds() {
    this.xLeft = this.element.getBoundingClientRect().left;
    this.xRight = this.element.getBoundingClientRect().right;
    this.yTop = this.element.getBoundingClientRect().top;
    this.yBottom = this.element.getBoundingClientRect().bottom;
  }

  attachListeners() {
    this.element.addEventListener("mousedown", (event) => {
      this.startDrag(event, this);
    });

    document.addEventListener("mousemove", (event) => {
      this.drag(event);
    });

    document.addEventListener("mouseup", () => {
      this.endDrag();
    });
    this.element.addEventListener("dblclick", (event) => {
      this.doubleClick(event, this);
    });

    // Add event listener to prevent drawing lines while dragging
    this.element.addEventListener("dragstart", (event) => {
      event.preventDefault();
    });
  }

  startDrag(event) {
    if (event.button !== 0) return; // Check if the left mouse button (button 0) is pressed
    event.preventDefault();
    this.activeFile = this.element;

    // Calculate the offset between the cursor and the top-left corner of the file, excluding the label height
    const fileRect = this.activeFile.getBoundingClientRect();
    const labelHeight = this.activeFile.querySelector("div").offsetHeight; // Height of the label
    this.dragOffset = {
      x: event.clientX - fileRect.left,
      y: event.clientY - fileRect.top - (3 * labelHeight) / 2, // Adjust for label height
    };

    let mouseMoved = false;

    // Attach mousemove event listener to track mouse movement
    const mouseMoveHandler = () => {
      mouseMoved = true;
      this.isDragging = true;
      document.removeEventListener("mousemove", mouseMoveHandler); // Remove the listener after mouse movement
    };
    document.addEventListener("mousemove", mouseMoveHandler);

    // Set a timeout to check for mouse movement after a short delay
    setTimeout(() => {
      if (this.activeFile && !mouseMoved) {
        this.isDragging = false; // If no mouse movement occurred, cancel dragging
      }
    }, 1); // Adjust the delay as needed

    // Attach mouseup event listener to end drag action
    document.addEventListener("mouseup", this.endDrag);
  }

  drag(event) {
    if (this.isDragging && this.activeFile) {
      // Get the width and height of the folder
      const fileWidth = this.activeFile.offsetWidth;
      const fileHeight = this.activeFile.offsetHeight;

      // Get the width of the left nav
      const leftNavWidth = document.querySelector(".left-nav").offsetWidth;
      const headerBarHeight =
        document.querySelector(".header-bar").offsetHeight;

      // Calculate the new position, considering the left nav width and header bar height
      let newX =
        event.clientX - this.dragOffset.x + window.pageXOffset - leftNavWidth;
      let newY =
        event.clientY -
        this.dragOffset.y +
        window.pageYOffset -
        headerBarHeight;

      // Ensure the folder remains within the viewport bounds
      newX = Math.max(0, Math.min(newX, window.innerWidth - fileWidth));
      newY = Math.max(0, Math.min(newY, window.innerHeight - fileHeight));

      // Apply the new position
      this.activeFile.style.left = newX + "px";
      this.activeFile.style.top = newY + "px";

      event.preventDefault();
    }
  }

  endDrag() {
    if (this.isDragging && this.activeFile) {
      const mouseX = event.clientX;
      const mouseY = event.clientY;

      // Check if the mouse pointer is within the bounds of any folder
      const folders = document.querySelectorAll(".folder");
      let droppedFolder = null;

      folders.forEach((folder) => {
        const rect = folder.getBoundingClientRect();
        if (
          mouseX >= rect.left &&
          mouseX <= rect.right &&
          mouseY >= rect.top &&
          mouseY <= rect.bottom
        ) {
          droppedFolder = folder;
        }
      });

      // If a folder is dropped, move the file to that folder
      if (droppedFolder) {
        // Perform the necessary actions to move the file to the dropped folder
        // For example, you can update the file's position to be within the dropped folder
        const folderRect = droppedFolder.getBoundingClientRect();
        const offsetX = folderRect.left - this.dragOffset.x;
        const offsetY = folderRect.top - this.dragOffset.y;

        this.activeFile.style.left = offsetX + "px";
        this.activeFile.style.top = offsetY + "px";

        // You may also want to update any internal data structures to reflect the file's new position

        // Optionally, trigger any relevant events or actions related to dropping the file into a folder
      }

      // Reset dragging state
      this.activeFile = null;
      this.isDragging = false;

      // Remove highlighting from folders
      folders.forEach((folder) => {
        folder.classList.remove("highlighted");
      });
    }
  }

  doubleClick(event, file) {
    const chance = Math.random() * 10;

    if (chance > 8) {
      this.instance.messageFromDeck();
      const messageToDisplay =
        this.instance.messages[this.instance.deckPosition];
      console.log("Message to display:", messageToDisplay); // Add this line to check the value of messageToDisplay
      alert(messageToDisplay);
      event.preventDefault();
    } else {
      event.preventDefault();

      this.content = document.createElement("p");
      this.content.classList.add("file-content");
      this.content.id = "file-content";
      document.body.appendChild(this.content);

      const paragraph = new Paragraph(
        AnimeBrat,
        PushyWiseman,
        SAMWiseman,
        GigiEngle
      );
      paragraph.display("file-content");

      const popup = new Popup(file.name, file.content);
      popup.show();
    }
  }
}
