class Paragraph {
  constructor(...sources) {
    this.markovModel = this.generateModel(...sources);
    this.output_text = this.generateText(this.markovModel);
  }

  generateModel(...args) {
    const words = args.join(" ").split(" ");
    const model = {};

    // loop through all the words except the last one.
    for (let i = 0; i < words.length - 1; i++) {
      const target_word = words[i];
      const next_word = words[i + 1];

      // if the model doesn't contain the target word, add it.
      if (!model[target_word]) {
        model[target_word] = [];
      }

      // add the next word to the possibilities for target_word
      model[target_word].push(next_word);
    }

    return model;
  }

  generateText(model, first_word) {
    // if first_word isn't provided use a random word in the model object
    first_word = first_word ?? this.pick(Object.keys(model));

    // start with the word passed in
    this.output_text = first_word;
    let current_word = first_word;
    for (let i = 0; i < 120; i++) {
      // verify that the current word is in the model
      if (!model[current_word]) break;

      // choose the next word by sampling from options in the model
      current_word = this.pick(model[current_word]);

      // append word to output
      this.output_text += " ";
      this.output_text += current_word;

      // if we get to a word that ends with "." we are done.
      const last_character = current_word.substr(current_word.length - 1);
      if (last_character === ".") {
        break;
      }
    }
    return this.output_text;
  }
  // creates a markov chain model based on one or more input strings
  pick(array) {
    const index = Math.floor(Math.random() * array.length);
    return array[index];
  }

  display(id) {
    const p = document.getElementById(id);
    if (p) {
      p.style = "font-size: 30px; margin: 10%; line-height: 1.5;";
      p.innerText = this.output_text;
      document.body.append(p);
    } else {
      console.error(
        `Failed to display content: Element with ID ${id} not found.`
      );
    }
  }
}
