document.addEventListener("DOMContentLoaded", function () {
  const addFolderBtn = document.getElementById("add-folder-btn");
  let popup = null; // Variable to store the active popup
  let isPopupOpen = false; // Flag to track if a popup is open

  if (addFolderBtn) {
    addFolderBtn.addEventListener("click", function handleClick() {
      // Check if a popup is already open
      if (!isPopupOpen) {
        popup = new TextInputPopup("New Folder", addFolderBtn);
        popup.show();

        // Set the flag to indicate that a popup is open
        isPopupOpen = true;

        // Add event listener for form submission
        popup.element.addEventListener(
          "submit",
          function handleFormSubmit(event) {
            event.preventDefault();
            const folderName = popup.objectName; // Use the objectName property of the popup
            if (folderName) {
              createNewFolder(folderName);
            }
            popup.close();
            isPopupOpen = false;
          }
        );
      }
    });
  }
});

