var centerPoint = view.center;

var numTentacles = 20;

var angleIncrement = 360 / numTentacles;

var tentacles = [];
for (var i = 0; i < numTentacles; i++) {
    var angle = angleIncrement * i;

    var tentacleLength = Math.random() * 100;

    var endPoint = centerPoint + new Point(Math.cos(angle) * tentacleLength, Math.sin(angle) * tentacleLength);

    var tentacle = new Path();
    tentacle.add(centerPoint);
    tentacle.add(endPoint);
    tentacle.strokeColor = 'orange';
    tentacle.strokeWidth = 200;

    tentacles.push(tentacle);
}


var sun = new Path.Circle(centerPoint, 100);
sun.fillColor = 'yellow'; 

var maxTentacleLength = 200;
var minTentacleLength = 100;

var animationSpeed = 0.03;

var phase = 0;

function onFrame(event) {
    phase += animationSpeed;


    var newTentacleLength = minTentacleLength + Math.sin(phase) * (maxTentacleLength - minTentacleLength);

    for (var i = 0; i < tentacles.length; i++) {
        var angle = angleIncrement * i;
        var endPoint = centerPoint + new Point(Math.cos(angle) * newTentacleLength, Math.sin(angle) * newTentacleLength);
        tentacles[i].lastSegment.point = endPoint;
    }
}

