    for (let y = 0; y < height; y += size + gap) {
      let n1 = noise(x * rez1, y * rez1, time);
      let n2 = noise(x * rez2 + 10000, y * rez2 + 10000, time);
      let n3 = noise(x * rez3 + 20000, y * rez3 + 20000, time);
      fill(n2 * 360, 200);
      push();
      translate(x, y);
      rotate((n2 - 0.5) * PI);


      // Coordinates
