let amplitude_slider;
let time_slider;
let frequency_slider;

let startX = 50;
let startY = 250;
let endX = 450;
let endY = 50;

function setup() {
  createCanvas(500, 300);

  createP("Amplitude");
  amplitude_slider = createSlider(0, 100, 50);

  createP("Time Speed");
  time_slider = createSlider(0, 100, 50);

  createP("Frequency");
  frequency_slider = createSlider(0, 100, 50);
}

function draw() {
  background(50);
  ellipseMode(CENTER);

  let amplitude = amplitude_slider.value() / 100;
  let timeFactor = map(time_slider.value(), 0, 100, 0, 0.005);
let frequency = map(frequency_slider.value(), 0, 100, 0.1, 0.01);

  noiseDetail(1, 0.5);

  fill(255);
  noStroke();

  for (let i = 0; i < 1; i += frequency) {
    let x = lerp(startX, endX, i);
    let y = lerp(startY, endY, i);

    let time = millis() * timeFactor; 

    let offsetX = noise(time + i * 10) * amplitude * 50;
    let offsetY = noise(time + i * 10 + 1000) * amplitude * 50;

    ellipse(x + offsetX, y + offsetY, 10, 10);
  }
}

class Shape {
  constructor(x, y, color1, color2, color3, shape, nShape, radiusInc, r) {
    this.x = x;
    this.y = y;

    this.nShape = nShape;
    this.radiusInc = radiusInc;

    this.r = r;

        this.color1 = color(color1)
        this.color2 = color(color2)
        this.color3 = color(color3)
    this.shapeColors = [this.color1, this.color2, this.color3];

    this.shape = shape;
    this.selectShape();
  }

  selectShape() {
    if (this.shape == 'c') {
      for (let i = this.nShape; i > 0; i--) {
        this.diameter = i * this.radiusInc;
        this.shapeColor1 = this.shapeColors[i % 3];

        push();
        translate(this.x, this.y);
        rotate(this.r);

        ellipseMode(CENTER);
        stroke(this.shapeColor1);
        fill(this.shapeColor1);

        ellipse(this.x, this.y, this.diameter);

        pop();
      }
    } else if (this.shape == 'sq') {
      for (let i = this.nShape; i > 0; i--) {
        this.diameter = i * this.radiusInc;
        this.shapeColor1 = this.shapeColors[i % 3];

        push();
        translate(this.x, this.y);
        rotate(this.r);

        rectMode(CENTER);
        stroke(this.shapeColor1);
        fill(this.shapeColor1);

        rect(this.x, this.y, this.diameter);

        pop();
      }
    } else {
      console.log("selectShape() no worky");
    }
  }
}

