let w = 600;
let h = 600;

function setup() {
  createCanvas(w, h);
}

function draw() {
  background(50);

  noStroke();
  ellipseMode(CENTER);

  let noiseFrequency = 0.5;

  // Arrays to store positions of circles
  let circleX = [];
  let circleY = [];

  // Generate circles and store their positions
  for (let i = 0; i < 100; i++) {
    let x = noise(i * noiseFrequency, 0) * w;
    let y = noise(i * noiseFrequency, 1000) * h;

    let diameter = random(5, 25); // Varying diameter

    // the colors also need to change
    colorMode(HSB, 255); // HSB color mode
    fill(random(255), 255, 255); //

    ellipse(x, y, diameter, diameter);

    // Store circle positions
    circleX.push(x);
    circleY.push(y);
  }

  // Draw lines between circles
  stroke(255);
  for (let i = 0; i < circleX.length; i++) {
    for (let j = i + 1; j < circleX.length; j++) {

      // Calculate distance between two circles
      let distance = dist(circleX[i], circleY[i], circleX[j], circleY[j]);

      // If distance is smaller than a range, draw a line
      if (distance < 50) {
        line(circleX[i], circleY[i], circleX[j], circleY[j]);
      }
    }
  }

  noLoop();
}

let cols, rows;
let size = 25;
let noiseScale = 0.2;

function setup() {
  createCanvas(600, 600);
  cols = floor((width + size) / size);
  rows = floor((height + size) / size);
  noiseDetail(4);
}

function draw() {
  background('#364652');

  for (let x = 0; x < cols; x++) {
    for (let y = 0; y < rows; y++) {
      let posX = x * size - size / 2;
      let posY = y * size - size / 2;
      let noiseVal = noise(x * noiseScale, y * noiseScale);
      let circleSize = map(noiseVal, 0, 1, size * 0.1, size * 2);

      // Define gradient colors
      let colorStart = color('#ede6c0');
      let colorEnd = color('#161d49');

      // Interpolate between the colors based on noise value
      let gradientColor = lerpColor(colorStart, colorEnd, noiseVal);

      // Apply gradient color to circles
      fill(gradientColor);
      stroke('#364652');
      ellipse(posX, posY, circleSize);
    }
  }
}

