function drawBody(x, y, size) {
  fill('green');
  stroke('black');
  strokeWeight(2);

  // Draw the main shell
  ellipse(x, y, size * 2, size * 1.6);

  // Draw the shell pattern
  strokeWeight(1);
  for (let i = -size; i < size; i += 20) {
    line(x + i, y - sqrt(size*size - i*i) * 0.8, x + i, y + sqrt(size*size - i*i) * 0.8);
  }
  for (let i = -size*0.8; i < size*0.8; i += 20) {
    line(x - sqrt(size*size - i*i/0.64), y + i, x + sqrt(size*size - i*i/0.64), y + i);
  }
}

function drawHead(x, y, size) {
  fill('yellow');
  stroke('black');
  strokeWeight(2);

  // Draw the head
  ellipse(x, y, size, size);

  // Draw the eyes
  fill('black');
  ellipse(x - size/4, y - size/8, size/10, size/10);
  ellipse(x + size/4, y - size/8, size/10, size/10);
}

function drawLegs(x, y, size) {
  fill('yellow');
  stroke('black');
  strokeWeight(2);

  // Draw the front legs
  ellipse(x - size, y - size/2, size/2, size/1.5);
  ellipse(x + size, y - size/2, size/2, size/1.5);

  // Draw the back legs
  ellipse(x - size, y + size/2, size/2, size/1.5);
  ellipse(x + size, y + size/2, size/2, size/1.5);
}

function drawTail(x, y, size) {
  fill('yellow');
  stroke('black');
  strokeWeight(2);

  // Draw the tail
  triangle(x, y, x - size/2, y + size, x + size/2, y + size);
}

