class Balls{
  constructor(size, velocity, r, x2, y2){
    this.theta = 0;
    this.r = r;
    this.x = 0;
    this.y = 0;
    this.size = size;
    this.velocity = velocity;
    this.x2 = x2;
    this.y2 = y2;
  }

  Color(){
    stroke(0, this.velocity*2000);
    fill(0, this.velocity*2000);
  }

  physics(){
    this.x = this.r*cos(this.theta);
    this.y = this.r*sin(this.theta);
    this.theta = this.theta + this.velocity;
  }

  display(){
    strokeWeight(this.size);
    line(0, 0, this.x, this.y);
    line(this.x, this.y, this.x2, this.y2);
  }
}

