function generateHaiku() {
  var syllables_one = [
    "oops", "yeah", "think", "made", "more", "lose", "oh", "heart", "cry", "days", "fool", "ways", "love", "lost", "game", "sent", "end"
  ];
  var syllables_two = [
    "again", "baby", "believe", "senses", "problem", "dreaming", "wishing", "heroes", "truly", "exist", "watching", "many", "above", "aboard", "ocean", "minute"
  ];
  var syllables_three = [
    "typically", "innocent", "serious", "beautiful"
  ];

  var haiku = "";

  var syllable_one1 = sample(syllables_one);
  var syllable_one2 = sample(syllables_one.filter(word => word !== syllable_one1));
  var syllable_one3 = sample(syllables_one.filter(word => word !== syllable_one1 && word !== syllable_one2));
  var syllable_one4 = sample(syllables_one.filter(word => word !== syllable_one1 && word !== syllable_one2 && word !== syllable_one3));
  var syllable_one5 = sample(syllables_one.filter(word => word !== syllable_one1 && word !== syllable_one2 && word !== syllable_one3 && word !== syllable_one4));

  var syllable_two1 = sample(syllables_two);
  var syllable_two2 = sample(syllables_two.filter(word => word !== syllable_two1));
  var syllable_two3 = sample(syllables_two.filter(word => word !== syllable_two1 && word !== syllable_two2));
  var syllable_three1 = sample(syllables_three);
  var syllable_three2 = sample(syllables_three.filter(word => word !== syllable_three1));

  haiku += `${syllable_one1} ${syllable_one2} ${syllable_two1} ${syllable_one3}<br>`;
  haiku += `${syllable_three1} ${syllable_one4} ${syllable_two2} ${syllable_one5} <br>`;
  haiku += `${syllable_three2} ${syllable_two3}`;

  return haiku;
}

function sample(array) {
  return array[Math.floor(Math.random() * array.length)];
}

