var video;
var vScale = 13; // Separate variable for scaling the video capture
var shapeSize = 10; // Initial size for the shape (ellipse)
var vScaleSlider;
var maxBright = 255; // Initial value for maxBright
var frameCounter = 0; // Counter to track frames
var canvasShape = 'rect'; // Initial canvas shape
var maskSize = 0; // Initial mask size

function setup() {
  createCanvas(820, 480); // Create a rectangular canvas by default
  pixelDensity(1);
  video = createCapture(VIDEO);
  video.size(width / vScale, height / vScale); // Use vScale for video capture size
}

function draw() {
  background(25);

  if (frameCounter % 6 === 0) { // Update every 6 frames
    shapeSize = noise(frameCount * 0.01) * 50; // Use noise() for dynamic shape size
  }

  video.loadPixels();
  loadPixels();

  // Draw video pixels
  for (var y = 0; y < video.height; y++) {
    for (var x = 0; x < video.width; x++) {
      var index = (x + y * video.width) * 4;
      var r = video.pixels[index + 0];
      var g = video.pixels[index + 1];
      var b = video.pixels[index + 2];
      var bright = (r + g + b) / 3;

      var shapeWidth = map(bright, 0, maxBright, 0, shapeSize); // Use shapeSize for shape width
      var shapeHeight = map(bright, 0, maxBright, 0, shapeSize); // Use shapeSize for shape height

      noStroke();
      fill(random(r, 0), random(g, 0), random(b, 0)); // Use the color from the video pixel

      if (canvasShape === 'rect') { // Draw video pixels only if canvas is rectangular
        rectMode(CORNER); // Set rectangle mode to corner
        rect(x * vScale, y * vScale, shapeWidth, shapeHeight); // Draw rectangle
      } else if (canvasShape === 'ellipse') {
        ellipseMode(CORNER); // Set ellipse mode to corner
        ellipse(x * vScale, y * vScale, shapeWidth, shapeHeight); // Draw ellipse
      }
    }
  }

  // Draw mask
  fill(0); // Black color for mask
  if (canvasShape === 'rect') { // Draw rectangle mask
    rect(0, 0, width, maskSize); // Draw rectangle mask
  } else if (canvasShape === 'ellipse') { // Draw ellipse mask
    ellipse(width/3, height/4, maskSize); // Draw ellipse mask
  }

  // Increment mask size based on frameCount
  maskSize = map(sin(frameCount * 0.04), -1, 1, 0, height);

  frameCounter++; // Increment frame counter

  // Change canvas shape based on frameCount
  if (frameCounter % 240 === 0) { // Change shape every 240 frames (approximately every 4 seconds)
    if (canvasShape === 'rect') {
      canvasShape = 'ellipse'; // Change canvas shape to ellipse
      createCanvas(820, 480); // Create a square canvas
    } else {
      canvasShape = 'rect'; // Change canvas shape back to rectangle
      createCanvas(820, 480); // Create a rectangular canvas
    }
  }
}

// require https://cdn.jsdelivr.net/npm/p5@1.4.0/lib/p5.js
let pointCount = 1000;
let minDist = 20;
let maxDist = 1000;
let points = [];
let pointSize = 10;
let sphereRad = 300;
let sat = 100; //change to 50 to make it look pastely
let discoBall = false;  //set true to animate color using noise, set false for location mapped colors

function setup() {
  createCanvas(800, 800, WEBGL);
}

function draw() {
  orbitControl();
  background(220);
  push();
  stroke('black');
  noFill();
  sphere(sphereRad);
  pop();

  placePointsCullRange(pointCount, minDist, maxDist);
  for (let i = 0; i < points.length; i++) {
    let x = points[i].x;
    let y = points[i].y;
    let z = points[i].z;

    let h = map(x, -sphereRad, sphereRad, 0, 360);
    let s = map(y, -sphereRad, sphereRad, 0, 360);
    let b = map(z, -sphereRad, sphereRad, 0, 360);
    let n = noise(i, millis() * .001);
    let col = map(n, 0.1, 0.8, 0, 360);

    push();
    colorMode(HSB);
    noStroke();
    if (discoBall) {
      fill(col, sat, 100);
    } else {
      fill(h, s, b);
    }
    translate(x, y, z);
    sphere(pointSize);
    pop();
  }
}

function placePointsCullRange(count, min_distance, max_distance) {
  let current_try = 0;
  let max_tries = count * 10;

  points.push({
    x: sin(0) * cos(0) * sphereRad,
    y: sin(0) * sin(0) * sphereRad,
    z: cos(0) * sphereRad
  });

  while (points.length < count && current_try < max_tries) {
    current_try++;

    let phi = random(0, TWO_PI);
    let theta = random(0, PI);
    let x = sin(theta) * cos(phi) * sphereRad;
    let y = sin(theta) * sin(phi) * sphereRad;
    let z = cos(theta) * sphereRad;

    let isTooClose = checkClose(x, y, z, points, min_distance);
    let isCloseEnough = checkClose(x, y, z, points, max_distance);

    if (!isTooClose && isCloseEnough) {
      points.push({
        x: x,
        y: y,
        z: z,
      });
    }
  }

  return points;
}

function checkClose(x, y, z, points, min_distance) {
  for (let i = 0; i < points.length; i++) {
    if (dist(points[i].x, points[i].y, points[i].z, x, y, z) < min_distance) {
      return true;
    }
  }
  return false;
}

