  let totalWidth = cols * gridSize;
  let totalHeight = rows * gridSize;

  let xOffset = (width - totalWidth) / 2;
  let yOffset = (height - totalHeight) / 2;
//chat gpt
  for (let i = 0; i < rows; i++) {
    for (let j = 0; j < cols; j++) {
      let x = xOffset + j * gridSize + gridSize / 2;
      let y = yOffset + i * gridSize + gridSize / 2;
      circles.push(new Circle(x, y, ""));
    }
  }
