    }
  } else {
    for (let i = 0; i < circles.length; i++) {
      fill(185, 218, 98);
      circles[i].updateTriangleFormation();
      circles[i].display();
    }

    if (!endAnimation) {
      count += 15;

      if (count > width) {
        count = 0;
      }

      tint(255, 120); //image opacity
      image(img, count, random(40) + height * 0.3);
    } else {
      textSize(64);
      text("comp form", width / 2, height / 2);
    }

    if (frameCount >= endAnimationFrame) {
      endAnimation = true;
    }
  }
}

class Circle {
  constructor(x, y, letter) {
    this.x = x;
    this.y = y;
    this.letter = letter;
    this.rotation = 100;
    this.targetX = x;
    this.targetY = y;
  }

  updateCircularFormation() {
    this.rotation += 0.02;
    //chatgpt
    let radius = 100;
    let angleIncrement = TWO_PI / 8;
    let index = circles.indexOf(this);
    this.x = 200 + radius * cos(index * angleIncrement + frameCount * 0.05);
    this.y = 200 + radius * sin(index * angleIncrement + frameCount * 0.05);
  }

  updateTriangleFormation() {
    this.x = lerp(this.x, this.targetX, 0.2);
    this.y = lerp(this.y, this.targetY, 0.2);
  }

  display() {
    let scaleValue = sin(this.rotation) * 0.5 + 1;
    image(img, this.x, this.y, 100 * scaleValue, 90 * scaleValue);
  }
}

