  let emoji = guessed && choice !== null ? ((currentNumber > 7 && choice === 'above') || (currentNumber < 7 && choice === 'below') ? correctEmoji : wrongEmoji) : '';
  text(`${emoji}`, width / 2, height / 2 + 10);
  pop();

  // Display the tally 
  text(`${tally}`, width / 2, height- 120 );
}

function submitGuess(guess) {
  if (!guessed) {
    choice = guess;
    if ((currentNumber > 7 && choice === 'above') || (currentNumber < 7 && choice === 'below')) {
      money += 1;
      tally += correctEmoji;
    } else {
      money -= 1;
      tally += wrongEmoji;
    }

    guessed = true;
    aboveButton.elt.disabled = true;
    belowButton.elt.disabled = true;

    setTimeout(() => {
      selectRandomNumber();
      aboveButton.elt.disabled = false;
      belowButton.elt.disabled = false;
      choice = null;
    }, 1000);
  }
}

