function drawDots(points) {
  background(0);

  noStroke();
  ellipseMode(CENTER);

  for (let i = 0; i < points.length; i++) {
    let point = points[i];

    let hue = noise(i * 0.1) * 25; 
    let saturation = random(50, 80); 
    let brightness = random(50, 100); 

    hue += random(-10, 10); 
    hue = constrain(hue, 0, 25);

    fill(hue, saturation, brightness);

    let diameter = random(5, 20); 
    ellipse(point.x, point.y, diameter, diameter);
  }
}

function placePointsNoise(count, w, h, frequency) {
  let points = [];

  for (let i = 0; i < count; i++) {
    let x = noise(i * frequency, 0) * w;
    let y = noise(i * frequency, 1000) * h;
    points.push({
      x: x,
      y: y,
    });
  }

  return points;
}

