function drawPolygons() {
  for (let i = 0; i < points.length /4; i++) {
    let currentPoint = points[i]; //first point
    let nextPoint = points[(i + 1) % points.length]; //second point
    let lastPoint = points[(i+3) % points.length]; //last point; needed to create polygon and to ensure each dot has 2 lines as per sketch instructions

    // Use the pre-generated colors for each polygon
    fill(difColors[i]);

    beginShape();
    vertex(currentPoint.x, currentPoint.y);
    vertex(nextPoint.x, nextPoint.y);
    vertex(lastPoint.x, lastPoint.y);
    endShape(CLOSE);

  line(currentPoint.x, currentPoint.y, nextPoint.x, nextPoint.y);
  line(nextPoint.x,nextPoint.y, lastPoint.x, lastPoint.y);
  }
}

function drawLines(){
  for (let i = 0; i < points.length/4; i++) {
    stroke(220); 
    //reusing the same method here I used for drawPolygons
    let currentPoint = points[i]; //first point
    let nextPoint = points[(i + 1) % points.length]; //second point
    let lastPoint = points[(i+3) % points.length]; //last point
  line(currentPoint.x, currentPoint.y, nextPoint.x, nextPoint.y);
