function spiralLine(centerX, centerY) {
	let maxR = width
	let angle = 0
	let r = 0

	while (r < maxR) {
		angle += 0.01
		r += 0.05
		let x = centerX + cos(angle) * r
		let y = centerY + sin(angle) * r

		stroke(map(r, 0, maxR, 0, 255), 120, map(r, 0, maxR, 50, 200))
		strokeWeight(4)
		circle(x, y, random(r / 3))
	}
}

