let particles = [];
let speedSlider, densitySlider;
let speedLabel, densityLabel;

function setup() {
  createCanvas(600, 600);

  // Create sliders
  speedSlider = createSlider(0, 5, 1, 0.1);
  densitySlider = createSlider(0, 200, 100, 1);

  // Position sliders
  speedSlider.position(450, 60);
  densitySlider.position(450, 100);

  // Create and position labels for the sliders
  speedLabel = createDiv('Particle Speed:').position(10, height + 5).style('color', 'black').style('font-family', 'Roboto, sans-serif');
  densityLabel = createDiv('Particle Density:').position(10, height + 35).style('color', 'black').style('font-family', 'Roboto, sans-serif');

  speedLabel.position(speedSlider.x, speedSlider.y - 20);
  densityLabel.position(densitySlider.x, densitySlider.y - 20);

  fillParticles();
}

function draw() {
  background(255);
  let particleSpeed = speedSlider.value();
  let particleDensity = densitySlider.value();

  // Adjust particle number according to density slider
  if (particles.length < particleDensity) {
    let additionalParticles = particleDensity - particles.length;
    for (let i = 0; i < additionalParticles; i++) {
      particles.push(new Particle(random(width), random(height)));
    }
  } else if (particles.length > particleDensity) {
    particles.splice(0, particles.length - particleDensity);
  }

  for (let i = 0; i < particles.length; i++) {
    for (let j = i + 1; j < particles.length; j++) {
      let d = dist(particles[i].x, particles[i].y, particles[j].x, particles[j].y);
      if (d < 50) {
        stroke(0, 102, 153, 50);
        line(particles[i].x, particles[i].y, particles[j].x, particles[j].y);
      }
    }
    particles[i].move(particleSpeed);
    particles[i].display();
  }
}

function fillParticles() {
  particles = [];
  for (let i = 0; i < densitySlider.value(); i++) {
    particles.push(new Particle(random(width), random(height)));
  }
}

class Particle {
  constructor(x, y) {
    this.x = x;
    this.y = y;
    this.vx = random(-1, 1);
    this.vy = random(-1, 1);
  }

  move(pSpeed) {
    this.x += this.vx * pSpeed;
    this.y += this.vy * pSpeed;

    // Bounce off edges
    if (this.x <= 0 || this.x >= width) this.vx *= -1;
    if (this.y <= 0 || this.y >= height) this.vy *= -1;
  }

  display() {
    noStroke();
    fill(0, 102, 153);
    ellipse(this.x, this.y, 4, 4);
  }
}

