function setQuick(img, x, y, c) {
  const i = (y * img.width + x) * 4;

  img.pixels[i + 0] = c[0];
  img.pixels[i + 1] = c[1];
  img.pixels[i + 2] = c[2];
  img.pixels[i + 3] = c[3];
}

function setImageQuick(img, r, g, b, a) {
  for (let y = 0; y < img.height; y++) {
    for (let x = 0; x < img.width; x++) {
      const c = getQuick(img, x, y);
      c[0] = r;
      c[1] = g;
      c[2] = b;
      c[3] = a;
      setQuick(img, x, y, c);
    }
  }
}

