  let color1 = color(249, 144, 187); // pink
  let color2 = color(229,134,94); // orange
  let color3 = color(239, 184, 76); // yellow


  // space between rectangles
  let space = 30;

  // height of rectangles
  let rectHeight1 = 300;
  let rectHeight2 = 150;

  // center rectangles as a group
  let groupHeight = rectHeight1 + rectHeight2 + space;
  let yPosition = (height - groupHeight) / 2;

  // rect 1 with grid
  drawGridRect(50, yPosition, width - 100, rectHeight1, color1, color2);

  // rect 2 with grid
  drawGridRect(50, yPosition + rectHeight1 + space, width - 100, rectHeight2, color3, color2);
}

function drawGridRect(x, y, w, h, c1, c2) { // x, y, width, height, color1, color2
  let cols = random(10,100); 
  let rows = random(4,8);

  // width and height of each grid cell
  let cellWidth = w / cols;
  let cellHeight = h / rows;

  // looping through each cell alternating between colors
  for (let i = 0; i < cols; i++) {
    for (let j = 0; j < rows; j++) {
      // color based on column index
      let col;
      if (i % 2 === 0) {
        col = (j % 2 === 0) ? c1 : c2;
      } else {
        col = (j % 2 === 0) ? c2 : c1;
      }

      // draw the rectangle
      fill(col);
      rect(x + i * cellWidth, y + j * cellHeight, cellWidth, cellHeight);
    }
  }
}

