let size = 100; // Initial size of the shape
let vertices = 3; // Initial number of vertices
let transitionFrames = 15; // Number of frames before adding one more vertex
let currentFrame = 0; // Counter for current frame
let shapeType = 'triangle'; // Initial shape type

function setup() {
  createCanvas(400, 400);
}

function draw() {
  background(220);

  // Calculate points for the current shape
  let shapePoints = [];
  for (let i = 0; i < vertices; i++) {
    let angle = map(i, 0, vertices, 0, TWO_PI);
    let x = width / 2 - sin(angle) * size;
    let y = height / 2 - cos(angle) * size;
    shapePoints.push(createVector(x, y));
  }

  // Draw the shape
  beginShape();
  for (let i = 0; i < shapePoints.length; i++) {
    vertex(shapePoints[i].x, shapePoints[i].y);
  }
  endShape(CLOSE);

  // Increment frame counter
  currentFrame++;

  // Check if it's time to add one more vertex
  if (currentFrame >= transitionFrames) {
    currentFrame = 0; // Reset frame counter

    // Switch between triangle and square
    if (shapeType === 'triangle') {
      vertices = 4; // Change to square
      shapeType = 'square';
    } else {
      vertices++; // Add one more vertex
      if (vertices > 12) { // Limit to 8 vertices
        vertices = 3; // Reset to triangle
      }
    }

    // Change the size of the shape for the next transition
    size = random(50, 150);
  }
}

