let offsetX = 0;
let offsetY = 0;
let offsetR = 0;
let nX;
let nY;
let nR;
let topColor 
let bottomColor 

function circle_1() {
  const steps = 20;
  const centerX = width / 2;
  const centerY = height / 2;
  const radius = 275;

  for (let step = 1; step <= steps; step++) {
    //noise offsets
    nX = noise(centerX * 0.1);
    offsetX += nX * 25;

    nY = noise(centerY * 0.01);
    offsetY += nY * 25 - 10;

    nR = noise(radius * 0.1);
    offsetR += nR * 2;

    const a1 = map(step - 1, 0, steps, 0, 2 * PI);
    const a2 = map(step, 0, steps, 0, 2 * PI) - offsetR;
    x1 = centerX + sin(a1 - nX) * nR;
    x2 = centerX + sin(a2) * radius;
    y1 = centerY + cos(a1) * nR;
    y2 = centerY + cos(a2 + nY) * radius;
    line(x1, y1, x2, y2);
  }

  for (let step = 1; step <= steps; step++) {
    //noise offsets
    nX = noise(centerX * 0.1);
    offsetX += nX * 25;

    nY = noise(centerY * 0.01);
    offsetY += nY * 25;

    nR = noise(radius * 0.1);
    offsetR += nR * 25;

    const a1 = map(step - 1, 0, steps, 0, 2 * PI);
    const a2 = map(step, 0, steps, 0, 2 * PI) - offsetR;
    x1 = centerX + sin(a1 - nX) * nR;
    x2 = centerX + sin(a2) * radius;
    y1 = centerY + cos(a1 - nY) * radius;
    y2 = centerY + cos(a2) * radius;
    line(x1, y1, x2, y2);
  }
}

