class Balls {
  constructor(x, y, velocity){
    this.x = x;
    this.y = y;
    this.radius = random(25, 40);
    this.velocity = velocity;
    this.colorChoice = floor(random(0, 8));
    this.directionX = random([-1, 1]);
    this.directionY = random([-1, 1]);
    this.shape = 'circle'; // Initial shape is circle
  }

  show(){
    noStroke();
    fill(colors[this.colorChoice]);
    if (this.shape === 'circle') {
      ellipse(this.x, this.y, this.radius * 2);
    } else if (this.shape === 'rectangle') {
      rectMode(CENTER);
      rect(this.x, this.y, this.radius * 2, this.radius * 2);
    } else if (this.shape === 'triangle') {
      triangle(this.x, this.y - this.radius, this.x - this.radius, this.y + this.radius, this.x + this.radius, this.y + this.radius);
    }
  }

  update(){
    this.x += this.directionX * this.velocity;
    this.y += this.directionY * this.velocity;

    // Bounce on vertical walls
    if (this.x <= this.radius || this.x >= width - this.radius){
      this.directionX *= -1;
      this.changeShape();
    }

    // Bounce on horizontal walls
    if (this.y <= this.radius || this.y >= height - this.radius){
      this.directionY *= -1;
      this.changeShape();
    }
  }

  animate(){
    // Additional animation logic can be added here if needed
  }

  changeShape(){
    if (this.shape === 'circle') {
      this.shape = 'rectangle';
    } else if (this.shape === 'rectangle') {
      this.shape = 'triangle';
    } else if (this.shape === 'triangle') {
      this.shape = 'circle';
    }
  }
}

