let sliderGrid;
let sliderShapes;
let imgs = [];
let shapes = [];

let prevNumShapes = 0; // Keep track of the previous number of shapes
let gridOccupied = []; // Keep track of occupied grid squares

function preload() {
  for (let i = 1; i <= 12; i++) {
    imgs.push(loadImage('assets/face-' + i + '.png'));
  }
}

function setup() {
  let canvas = createCanvas(600, 600);
  canvas.parent('sketch-container');

  sliderGrid = createSlider(4, 64, 4); 
  sliderGrid.style('width', '200px');
  sliderGrid.class('slider');
  sliderGrid.parent('slider-grid-container');

  sliderShapes = createSlider(0, 64, 0);
  sliderShapes.style('width', '200px');
  sliderShapes.class('slider'); 
  sliderShapes.parent('slider-shapes-container');

  // Add labels for sliders
  let gridSizeLabel = createSpan('Grid Size');
  gridSizeLabel.parent('slider-grid-container');
  gridSizeLabel.class('slider-label');

  let shapesLabel = createSpan('Number of Shapes');
  shapesLabel.parent('slider-shapes-container');
  shapesLabel.class('slider-label');
}


function draw() {
  background(0);

  let parts = sliderGrid.value();
  let gridSize = floor(sqrt(parts));

  let squareWidth = width / gridSize;
  let squareHeight = height / gridSize;


  // Only clear and repopulate shapes array if number of shapes has changed
  let numShapes = sliderShapes.value();
  if (numShapes !== prevNumShapes) {
    shapes = [];
    gridOccupied = []; // Clear the gridOccupied array

    for (let i = 0; i < numShapes; i++) {
      let gridX, gridY;
      let attempts = 0;

      do {
        gridX = floor(random(gridSize));
        gridY = floor(random(gridSize));
        attempts++;
      } while (gridOccupied[gridX + gridY * gridSize] && attempts < 100); 

      if (attempts < 100) {

        // If found an empty grid square, mark it as occupied
        gridOccupied[gridX + gridY * gridSize] = true;

        // Calculate position of the shape within the grid square
        let x = gridX * squareWidth + squareWidth / 2;
        let y = gridY * squareHeight + squareHeight / 2;

        // Calculate max size of shape to fit in grid square
        let maxSize = min(squareWidth, squareHeight);
        let size = maxSize;


        // Randomly select shape type
        let type = floor(random(3)); 

        shapes.push({
          x: x,
          y: y,
          size: size,
          type: type,
        });
      }
    }

    // Update the previous number of shapes
    prevNumShapes = numShapes;
  }

  // Draw images
  for (let y = 0; y < gridSize; y++) {
    for (let x = 0; x < gridSize; x++) {
      let index = y * gridSize + x;
      let imgIndex = index % imgs.length;

      let sx = map(x, 0, gridSize, 0, imgs[imgIndex].width);
      let sy = map(y, 0, gridSize, 0, imgs[imgIndex].height);
      let sw = imgs[imgIndex].width / gridSize;
      let sh = imgs[imgIndex].height / gridSize;

      let dx = x * squareWidth;
      let dy = y * squareHeight;
      let dw = squareWidth;
      let dh = squareHeight;

      image(imgs[imgIndex], dx, dy, dw, dh, sx, sy, sw, sh);
    }
  }

  // colors for shapes
let shapeColors = [
  color(0, 209, 114,125), // green
  color(0, 209, 114), // green 
  // color(85, 125, 183,150), //
];

// draw shapes
for (let i = 0; i < shapes.length; i++) {
  let shape = shapes[i];
  let shapeColor = color(shapeColors[i % shapeColors.length]); // Use modulo to cycle through colors

  fill(shapeColor);
  noStroke();
  if (shape.type === 0) {
    ellipse(shape.x, shape.y, shape.size, shape.size);
  } else if (shape.type === 1) {
    rect(shape.x - shape.size / 2, shape.y - shape.size / 2, shape.size, shape.size);
  } else if (shape.type === 2) {
    let halfSize = shape.size / 2;
    triangle(shape.x - halfSize, shape.y + halfSize, shape.x + halfSize, shape.y + halfSize, shape.x - halfSize, shape.y - halfSize);
  }
 }
}

