  var x2 = width * 0.75;
  var y1 = height * 0.25;
  var y2 = height * 0.75;
  var rotationSpeed1 = random(0, 0.1);
  var rotationSpeed2 = random(0, 0.1);
  var rotationSpeed3 = random(0, 0.1); 
  var rotationSpeed4 = random(0, 0.1);
  var hue1 = 0; 
  var hue2 = 0; 
  var hue3 = 0; 
  var hue4 = 0; 
  patterns.push(new Phyllotaxis(x1, y1, rotationSpeed1, hue1));
  patterns.push(new Phyllotaxis(x2, y1, rotationSpeed2, hue2));
  patterns.push(new Phyllotaxis(x1, y2, rotationSpeed3, hue3)); 
  patterns.push(new Phyllotaxis(x2, y2, rotationSpeed4, hue4)); 
}

function draw() {
  background(0);

  // Draw all patterns
  for (var i = 0; i < patterns.length; i++) {
    patterns[i].update();
    patterns[i].display();
  }
}

// Phyllotaxis class
class Phyllotaxis {
  constructor(x, y, rotationSpeed, hue) {
    this.x = x;
    this.y = y;
    this.n = 0;
    this.c = 3;
    this.rotationSpeed = rotationSpeed;
    this.hue = hue; // Store the hue value for this pattern
  }

  update() {
    this.n += 5;
  }

  display() {
    push();
    translate(this.x, this.y);
    rotate(this.n * this.rotationSpeed);
    for (var i = 0; i < this.n; i++) {
      var a = i * 137.3; // Random angle between 130 and 140
      var r = this.c * sqrt(i);
      var x = r * cos(a);
      var y = r * sin(a);
      fill(0, 0, 255); // Set saturation and brightness to maximum
      noStroke();
      ellipse(x, y, this.c + 1, this.c + 1);
    }
    pop();
  }
}

