let myTurtle;

function setup() {
  createCanvas(1000, 1000);
  noFill();
  background(255);
  noLoop();
  angleMode(DEGREES);
  myTurtle = new Turtle();
}

function draw() {
  background(255); // Dark background to represent soil
  for (let i = 0; i < 5; i++) { // Draw 5 trees to create a small forest
    myTurtle.penUp();
    // Randomize the starting position
    myTurtle.moveTo(random(width), 800); 
    myTurtle.turnTo(-90);
    myTurtle.penDown();
    drawBranch(random(50, 200)); // Randomize the size of the trees
  }
}

function drawBranch(length) {
  if (length < 20) {
    // Instead of stopping, draw a flower or leaf
    drawFlower();
    return;
  }
  // draw this branch
  myTurtle.moveForward(length);

  // left child
  myTurtle.pushState();
  myTurtle.turnLeft(60);
  drawBranch(length * 0.75);
  myTurtle.popState();

  // right child
  myTurtle.pushState();
  myTurtle.turnRight(35);
  drawBranch(length * 0.75);
  myTurtle.popState();
}

function drawFlower() {
  // Set the color for the cherry blossom petals
  fill(255, 183, 197); // Soft pink
  stroke(255, 183, 197); // Soft pink outline

  myTurtle.pushState();
  for (let i = 0; i < 10; i++) { // Draw a six-petal flower
    myTurtle.moveForward(10);
    myTurtle.moveBackward(10);
    myTurtle.turnRight(60);
  }
  // Draw the center of the flower
  fill(255, 215, 0); // Yellow center
  stroke(255, 215, 0); // Yellow outline
  strokeWeight(3);
  ellipse(myTurtle.x, myTurtle.y, 3, 3); // Small circle for the center

  myTurtle.popState();
}

