let cols, rows;
let scl = 20; // Scale of each cell in the grid
let w = 1200; // Width of the grid
let h = 800; // Height of the grid

let terrain = [];
let treasureX, treasureY, treasureZ;

function setup() {
  createCanvas(800, 600, WEBGL);
  cols = w / scl;
  rows = h / scl;

  // Generate the terrain heights
  for (let x = 0; x < cols; x++) {
    terrain[x] = [];
    for (let y = 0; y < rows; y++) {
      terrain[x][y] = map(noise(x * 0.1, y * 0.1), 0, 1, -120, 120); // Height range
    }
  }

  // Randomly place the treasure
  treasureX = int(random(cols)) * scl;
  treasureY = int(random(rows)) * scl;
  treasureZ = terrain[treasureX / scl][treasureY / scl]; // Get the Z height from the terrain

  noLoop(); // We only need to draw the map once
}

function draw() {
  background(135, 206, 235); // Sky color
  translate(0, 50, -200);
  rotateX(PI / 3.5); // Tilt the terrain for better viewing
  translate(-w / 2, -h / 2);

  // Draw the terrain
  drawTerrain();

  // Draw the treasure
  drawTreasure();
}

function drawTerrain() {
  for (let y = 0; y < rows - 1; y++) {
    beginShape(TRIANGLE_STRIP);
    for (let x = 0; x < cols; x++) {
      fill(terrainColor(terrain[x][y]));
      vertex(x * scl, y * scl, terrain[x][y]);
      fill(terrainColor(terrain[x][y + 1]));
      vertex(x * scl, (y + 1) * scl, terrain[x][y + 1]);
    }
    endShape();
  }
}

function drawTreasure() {
  push();
  noStroke(); // Disable outline
  fill(255, 0, 0); // Solid red color
  // Adjust Z to be on the surface by adding half the sphere's diameter to the terrain height
  translate(treasureX - w / 2, treasureY - h / 2, treasureZ + scl * 0.5);
  sphere(scl * 0.5); // Draw sphere with a diameter of half the cell size
  pop();
}


function terrainColor(elevation) {
  // Define color based on the elevation
  if (elevation <= 0) {
    return color(50, 50, 255); // Water
  } else if (elevation < 20) {
    return color(240, 220, 130); // Beach
  } else if (elevation < 50) {
    return color(34, 139, 34); // Forest
  } else {
    return color(139, 137, 137); // Mountain
  }
}

