  radius += cos(frameCount * 0.01); // Update the radius based on a cosine function

  // Update and display the balls for each array
  for (let k = 0; k < numArrays; k++) {
    for (let i = 0; i < totalPoints; i++) {
      ballsArrays[k][i].Color();
      ballsArrays[k][i].physics();
      ballsArrays[k][i].display(radius);
    }
  }
}class Balls {
  constructor(size, velocity, initialTheta) {
    this.theta = initialTheta; // Initial starting angle for each ball
    this.size = size;
    this.velocity = velocity;
  }

  Color() {
    noStroke();
    fill(255, this.velocity * 5000);
  }

  physics() {
    this.theta += this.velocity; // Update theta with constant velocity
  }

  display(radius) {
    let x = radius * cos(this.theta); // Apply the updated radius
    let y = radius * sin(this.theta); // Apply the updated radius
    strokeWeight(2);
    ellipse(x, y, this.size); // Display balls as points
  }
}

